import * as React from 'react';
import { View,  StyleSheet, Dimensions, SafeAreaView, VirtualizedList } from 'react-native';
import { FAB, Avatar, Button, Card, IconButton, Colors, Dialog, Portal, Provider, Paragraph, Divider, ProgressBar, Text } from 'react-native-paper';
import Ionicons from 'react-native-vector-icons/Ionicons';
import { LineChart, BarChart, PieChart, ProgressChart, ContributionGraph, StackedBarChart} from "react-native-chart-kit";


const DATA = [];

const getItem = (data, index) => ({
  id: Math.random().toString(12).substring(0),
  title: `Item ${index+1}`
});

const getItemCount = (data) => 50;

const Item = ({ title }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
  </View>
);




export default function ProgresoScreen({ navigation }) {
    const screenWidth = Dimensions.get("window").width;

    const dataChart = {
        labels: ["Intento 1", "Intento 2", "Intento 3"], // optional
        data: [0.4, 0.6, 0.8]
      };

      const chartConfig = {
        backgroundGradientFrom: "#ffff",
        backgroundGradientFromOpacity: 0,
        backgroundGradientTo: "#ffff",
        backgroundGradientToOpacity: 1,
        useShadowColorFromDataset: true,	
        color: (opacity = 1) => `rgba(46, 125, 50, ${opacity})`,
        labelColor: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
        strokeWidth: 3, // optional, default 3
        barPercentage: 0.5,
        useShadowColorFromDataset: false, // optional
        propsForDots: {
            r: "6",
            strokeWidth: "2",
            stroke: "#218838"
          }
      };

    return (
        <SafeAreaView style={ styles.container}>
           <Card style={styles.item} >
                <Card.Title title="Aciertos Por Intento"  />
                <Card.Content>

                   <View style={styles.barChart} >
                   <ProgressChart
                        data={dataChart}
                        width={screenWidth}
                        height={220}
                        strokeWidth={10}
                        radius={28}
                        chartConfig={chartConfig}
                        hideLegend={false}
                        style={{marginHorizontal: -70}}
                    />
                   </View>

                   


                </Card.Content>
                <Card.Actions>
                <Button color={Colors.amber800} >Anillos</Button>
                <Button color={Colors.amber800} >Puntos</Button>
                </Card.Actions>
                
            </Card>
        </SafeAreaView>
        
    );
}


const styles = StyleSheet.create({
   
    container: {
        flex: 1
      },
      item: {
        padding: 5,
        marginVertical: 8,
        marginHorizontal: 10,
        elevation: 5,
      },
      textIntento: {
        padding: 5,
      },

     barChart: {
          marginRight: 50
      }
  })
