import * as React from 'react';
import { View,  StyleSheet, Dimensions } from 'react-native';
import { FAB, Avatar, Button, Card, IconButton, Colors, Dialog, Portal, Provider, Paragraph, Divider, ProgressBar, Text } from 'react-native-paper';
import Ionicons from 'react-native-vector-icons/Ionicons';
import { LineChart, BarChart, PieChart, ProgressChart, ContributionGraph, StackedBarChart} from "react-native-chart-kit";

export default function ProgresoScreen({ navigation }) {
    const screenWidth = Dimensions.get("window").width;

    const data = {
        labels: ["January", "February", "March"],
        datasets: [
          {
            data: [60, 45, 38]
          }
        ]
      };

      const chartConfig = {
        backgroundGradientFrom: "#1E2923",
        backgroundGradientFromOpacity: 0,
        backgroundGradientTo: "#08130D",
        backgroundGradientToOpacity: 1,
        color: (opacity = 4) => `rgba(2, 120, 189, ${opacity})`,
        strokeWidth: 3, // optional, default 3
        barPercentage: 0.5,
        useShadowColorFromDataset: false // optional
      };

    return (
        <View style={ styles.container}>
           <Card style={styles.item} >
                <Card.Title title="Aciertos Por Intento"  />
                <Card.Content>

                   <View style={styles.barChart} >
                   <BarChart
                    data={data}
                    width={screenWidth}
                    height={220}
                    yAxisLabel="$"
                    chartConfig={chartConfig}
                    verticalLabelRotation={30}
                    />
                   </View>

                   


                </Card.Content>
                
            </Card>
        </View>
    );
}


const styles = StyleSheet.create({
   
    container: {
        flex: 1
      },
      item: {
        padding: 5,
        marginVertical: 8,
        marginHorizontal: 10,
        elevation: 5,
      },
      textIntento: {
        padding: 5,
      },

     barChart: {
          marginRight: 50
      }
  })
